import lombok.Data;enum Type {BALLPOINT, ROLLERBALL, FOUNTAIN, FELTTIP}enum Color {BLUE, BLACK, RED, PINK, YELLOW, WHITE}enum Brand {DALSEY, LAMY, PARKER, WATERMAN, ACHE, SENATOR, DELTA, MARLEN, MANZONI, PELICAN, PIERRE_CARDIN, ROTRING}@Datapublic class Pen {    private Type type;    private Color inkColor;    private double tipSize;    private Brand brand;    public Pen(Type type, Color inkColor, double tipSize, Brand brand) {        this.type = type;        this.inkColor = inkColor;        this.tipSize = tipSize;        this.brand = brand;    }    @Override    public String toString() {        return "The pen of monufacturing of the company " + this.brand + ", "                + this.type + " with tip size of " + this.tipSize + ", " + this.inkColor + " ink color";    }    @Override    public int hashCode() {        return super.hashCode();    }    @Override    public boolean equals(Object obj) {        return super.equals(obj);    }}