package hometask2.task1;enum Type {BALLPOINT, ROLLERBALL, FOUNTAIN, FELTTIP}enum Color {BLUE, BLACK, RED, PINK, YELLOW, WHITE}enum Brand {DALSEY, LAMY, PARKER, WATERMAN, ACHE, SENATOR, DELTA, MARLEN, MANZONI, PELICAN, PIERRE_CARDIN, ROTRING}public class Pen {    private Type type;    private Color inkColor;    private double tipSize;    private Brand brand;    Pen(Type type, Color inkColor, double tipSize, Brand brand) {        this.type = type;        this.inkColor = inkColor;        this.tipSize = tipSize;        this.brand = brand;    }    @Override    public String toString() {        return "The pen of monufacturing of the company " + this.brand + ", "                + this.type + " with tip size of " + this.tipSize + ", " + this.inkColor + " ink color";    }    @Override    public boolean equals(Object obj) {        if (this == obj)            return true;        if (obj == null)            return false;        if (getClass() != obj.getClass())            return false;        Pen other = (Pen) obj;        return type == other.type &&                inkColor == other.inkColor &&                !(tipSize != other.tipSize) &&                brand == other.brand;    }    @Override    public int hashCode() {        final int prime = 31;        int result;        Double temp = tipSize;        result = type.hashCode();        result = prime * result + inkColor.hashCode();        result = prime * result + temp.hashCode();        result = prime * result + brand.hashCode();        return result;    }}