package hometask2.task2;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;public class Main {    public static void main(String[] args) {        Office office = new Office();        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));        OfficeService.generateEmployees(office);        try {            while (true) {                int i = 1;                System.out.format("%n* what would you like to do? Type command 1 or 2");                System.out.format("%n%d  Show the list employees", i++);                System.out.format("%n%d  Exit%n%n", i);                int n = OfficeService.enterInt(reader);                while (n < 1 || n > i) {                    System.out.format("%n* Type command 1 or 2...%n");                    n = OfficeService.enterInt(reader);                }                switch (n) {                    case 1: {                        if (OfficeService.printEmployeeList(office.getEmplList()) == 1)                            OfficeService.chooseEmployee(office.getEmplList(), reader);                        continue;                    }                    case 2: {                        System.out.println("* Goodbye!");                        reader.close();                        return;                    }                }            }        } catch (IOException e) {            System.out.println(e.getMessage());        }    }}