package hometask2.task2;import java.io.IOException;public class WorkDeskService {    static void addItem(Employee employee, Item item) {        employee.getEmplStationary().add(item);    }    static double getTotalPrice(Employee employee) {        double totalPrice = 0;        for (Item item : employee.getEmplStationary()) {            totalPrice += item.getItemPrice() * item.getQuantity();        }        return totalPrice;    }    /**     * Method prints content of a note     *     * @param employee - particular employee     * @throws IOException for BufferedReader     */    static void printStationaryList(Employee employee) throws IOException {        System.out.println("TYPE           COLOR   QUANTITY  PRICE");        for (Item item : employee.getEmplStationary()) {            System.out.print(                    item.getStationary() +                            OfficeService.createGap(15, item.getStationary().name()) +                            item.getColor() +                            OfficeService.createGap(8, item.getColor().name()) +                            item.getQuantity() +                            OfficeService.createGap(10, String.valueOf(item.getQuantity()))                            );            System.out.format("%.2f%n", +item.getItemPrice());        }        System.out.format("Total price: %.2f%n", WorkDeskService.getTotalPrice(employee));    }}