package hometask2.task3;import lombok.Data;enum Color {BLUE, BLACK, RED, PINK, YELLOW, WHITE}enum Format {OTHER, SQUARE, A1, A2, A3, A4, A5}enum PaperItemType {PRINTERPAPER, NOTEBOOK, STICKERPACK, GRAPHICPAPER}@Datapublic class PaperItem extends Item {    PaperItemType type;    Color color;    int sheetsNumberInPack;    Format format;    double height;    double width;    public PaperItem(PaperItemType type,                     Color color,                     int sheetsNumberInPack,                     Format format,                     double price) {        this.type = type;        this.color = color;        this.sheetsNumberInPack = sheetsNumberInPack;        this.format = format;        this.price = price;    }    public PaperItem(PaperItemType type,                     Color color,                     int sheetsNumberInPack,                     double height,                     double width,                     double price) {        this.type = type;        this.color = color;        this.sheetsNumberInPack = sheetsNumberInPack;        this.height = height;        this.width = width;        this.price = price;        this.format = Format.OTHER;    }    public PaperItem(PaperItemType type,                     Color color,                     int sheetsNumberInPack,                     double height,                     double price) {        this.color = color;        this.sheetsNumberInPack = sheetsNumberInPack;        this.height = height;        this.width = height;        this.format = Format.SQUARE;        this.price = price;    }}