package hometask2.task3.writingItem;enum PenType {BALLPOINT, ROLLERBALL, FOUNTAIN, FELTTIP}enum PenBrand {DALSEY, LAMY, PARKER, WATERMAN, ACHE, SENATOR, DELTA, MARLEN, MANZONI, PELICAN, PIERRE_CARDIN, ROTRING}public class Pen extends WritingItem {    PenType penType;    PenBrand penBrand;    public Pen(PenType penType, PenBrand penBrand, WritingColor writingColor, double price) {        this.penType = penType;        this.penBrand = penBrand;        this.price = price;        this.writingColor = writingColor;        this.permanent = true;    }}