package hometask2.task3.writingItem;enum Hardness {    _9H, _8H, _7H, _6H, _5H, _4H, _3H, _2H, _H, _F,    _HB, _B, _2B, _3B, _4B, _5B, _6B, _7B, _8B, _9B}enum PencilBrand {    PALOMINO, OZARK, TOMBOW, CHIKYU, STAEDTLER, APSARA, NATARAJ, ADEL}public class Pencil extends WritingItem {    Hardness hardness;    PencilBrand pencilBrand;    public Pencil(Hardness hardness, PencilBrand pencilBrand, WritingColor writingColor, double price) {        this.hardness = hardness;        this.pencilBrand = pencilBrand;        this.writingColor = writingColor;        this.price = price;        this.permanent = false;    }}