package hometask2.task5;import hometask2.task5.student.*;import hometask2.task5.subject.*;import java.util.ArrayList;import java.util.Arrays;import java.util.Collections;import java.util.Random;public class ProjectService {    static Random random = new Random();    static int studentsNumber = 10;    public static void createDemo(ArrayList<Student> studentArrayList,                                  ArrayList<Subject> subjectArrayList) {        for (int i = 1 ; i < studentsNumber ; i++) {            Student student = new Student(Name.values()[random.nextInt(Name.values().length)],                    LastName.values()[random.nextInt(LastName.values().length)]);            studentArrayList.add(student);        }        Subject COMPUTER_SCIENCE = new DoubleSubject(SubjectName.COMPUTER_SCIENCE, 100);        subjectArrayList.add(COMPUTER_SCIENCE);        pairStudentSubject(studentArrayList, COMPUTER_SCIENCE);        Subject MATH = new DoubleSubject(SubjectName.MATH, 100);        subjectArrayList.add(MATH);        pairStudentSubject(studentArrayList, MATH);        Subject ART = new DoubleSubject(SubjectName.ART, 100);        subjectArrayList.add(ART);        pairStudentSubject(studentArrayList, ART);        Subject PHYSICS = new IntegerSubject(SubjectName.PHYSICS, 2, 10);        subjectArrayList.add(PHYSICS);        pairStudentSubject(studentArrayList, PHYSICS);        Subject LANGUAGE = new IntegerSubject(SubjectName.LANGUAGE, 2, 5);        subjectArrayList.add(LANGUAGE);        pairStudentSubject(studentArrayList, LANGUAGE);    }    private static void pairStudentSubject(ArrayList<Student> studentArrayList,                                           Subject subject) {        Integer[] arr = new Integer[studentsNumber];        for (int i = 0 ; i < arr.length ; i++) {            arr[i] = i;        }        for (int i = 0 ; i < 10 ; i++) {            Collections.shuffle(Arrays.asList(arr));            StudentService.pairSubjectWithStudent(studentArrayList.get(arr[i]), subject);            SubjectService.pairStudentWithSubject(subject, studentArrayList.get(arr[i]));            StudentService.pairStudentSubjectWithMark(studentArrayList.get(arr[i]),                    subject,                    subject.getMaxMark());        }    }}