package hometask2.task5.student;import hometask2.task5.subject.Mark;import hometask2.task5.subject.Subject;import hometask2.task5.*;import lombok.Data;import java.util.ArrayList;import java.util.List;public class Student {    private Name name;    private LastName lastName;    private List<Subject> subjectList;    private List<Mark> studentMarks;    public Student(Name name, LastName lastName) {        this.name = name;        this.lastName = lastName;        this.subjectList = new ArrayList<>();        this.studentMarks = new ArrayList<>();    }    public Name getName() {        return name;    }    public LastName getLastName() {        return lastName;    }    public List<Subject> getSubjectList() {        return subjectList;    }    public List<Mark> getStudentMarks() {        return studentMarks;    }}