package hometask2.task5.student;import hometask2.task5.subject.Mark;import hometask2.task5.subject.Subject;public class StudentService {    public static void pairSubjectWithStudent(Student student,                                              Subject subject) {        student.getSubjectList().add(subject);    }    public static void pairStudentSubjectWithMark(Student student,                                                  Subject subject,                                                  Double mark) {        Mark subMark = new Mark(subject.getSubjectName().ordinal(), mark);        student.getStudentMarks().add(student.getSubjectList().indexOf(subject), subMark);    }}