package hometask2.task5.student;import hometask2.task5.subject.Subject;public class StudentService {    public static void pairSubjectWithStudent(Student student,                                              Subject subject) {        student.subjectList.add(subject);    }    public static void pairStudentSubjectWithMark(Student student,                                                  Subject subject,                                                  Double mark) {        student.studentMarks.add(student.subjectList.indexOf(subject), mark);    }}