package hometask2.task5.subject;import lombok.Data;@Datapublic class DoubleSubject extends Subject {    private double maxMark;    private double minMark;    public DoubleSubject(SubjectName subjectName, double maxMark) {        this.maxMark = maxMark;        this.subjectName = subjectName;        this.isDoubleMark = true;        this.minMark = 0;    }    public boolean getIsDoubleMark() {        return isDoubleMark;    }    public double getMaxMark() {        return maxMark;    }    public double getMinMark() {        return minMark;    }}