package hometask2.task5.subject;import java.util.Comparator;public class Mark implements Comparable<Mark> {    private int subjectIndex;    private double mark;    public Mark(int subjectIndex, double mark) {        this.subjectIndex = subjectIndex;        this.mark = mark;    }    public int getSubjectIndex() {        return subjectIndex;    }    public double getMark() {        return mark;    }    @Override    public int compareTo(Mark o) {        return (int) (100 * (o.mark - this.mark));    }}