package task2;enum Stationary {PEN, RUB, PENCIL, RULER, STICKERS}enum Color {NULL,RED, BLUE, WHITE, BLACK, ORANGE, YELLOW}public class Item {    private Stationary stationary;    private Color color;    private double itemPrice;    private int quantity;    public Item(Stationary stationary, Color color, double itemPrice, int quantity) {        this.stationary = stationary;        this.color = color;        this.itemPrice = itemPrice;        this.quantity = quantity;    }    public Item(Stationary stationary, double itemPrice, int quantity) {        this.stationary = stationary;        this.itemPrice = itemPrice;        this.quantity = quantity;        this.color = Color.NULL;    }    public Stationary getStationary() {        return stationary;    }    public Color getColor() {        return color;    }    public double getItemPrice() {        return itemPrice;    }    public int getQuantity() {        return quantity;    }}