package task2;import java.io.IOException;public class WorkDeskService {    static void addItem(Employee employee, Item item) {        employee.getEmplStationary().add(item);    }    static double getTotalPrice(Employee employee) {        int totalPrice = 0;        for (Item item : employee.getEmplStationary()) {            totalPrice += item.getItemPrice() * item.getQuantity();        }        return totalPrice;    }    /**     * Method prints content of a note     *     * @param employee - particular employee     * @throws IOException for BufferedReader     */    static void printStationaryList(Employee employee) throws IOException {        System.out.println("stationary type     color   quantity    price");        for (Item item : employee.getEmplStationary()) {            System.out.print(item.getStationary() + " "                    + item.getColor() + "   "                    + item.getQuantity());            System.out.format("   %.2f", +item.getItemPrice());        }        System.out.format("Total price: %.2f%n", WorkDeskService.getTotalPrice(employee));    }}