package hometask3.task1;import java.util.ArrayList;import java.util.List;public class CrazyLogger implements CrazyLoggerImpl {    private StringBuilder logger = new StringBuilder();    @Override    public String getDate(String msg, StringBuilder stringBuilder) {        int cutIndex = stringBuilder.lastIndexOf(msg);        String subString = stringBuilder.substring(0,cutIndex);        int startIndex = subString.lastIndexOf(';') + 1;        return subString.substring(startIndex,cutIndex - 2);    }    @Override    public String getMsg(String date, StringBuilder stringBuilder) {        int startIndex = stringBuilder.lastIndexOf(date);        String subString = stringBuilder.substring(startIndex + 1);        int cutIndex = subString.indexOf(';');        return subString.substring(startIndex + date.length() + 4,cutIndex);    }    @Override    public List<String> getMsgsInInterval(String date1, String date2, StringBuilder stringBuilder) {        List<String> messageList = new ArrayList<>();        int startIndex = stringBuilder.indexOf(date1);        int endIndex = stringBuilder.lastIndexOf(date2);        String intervalSubString = stringBuilder.substring(startIndex,endIndex);        int index = 0;        while (intervalSubString.indexOf(';',index) != -1) {            String msg = stringBuilder.substring(index, intervalSubString.indexOf(';',index));            messageList.add(msg);            index = intervalSubString.indexOf(';',index) + 1;        }        return messageList;    }    public StringBuilder getLogger() {        return logger;    }}