package hometask3.task1;import java.text.SimpleDateFormat;import java.util.Calendar;import java.util.Date;public class Main {    public static void main(String[] args) {        CrazyLogger crazyLogger = new CrazyLogger();        SimpleDateFormat dateFormat = new SimpleDateFormat("dd-MM-yyyy : hh-mm ");        for (int i = 0 ; i < 10 ; i++) {            String string = dateFormat.format(new Date(i * 100000000000L)) +                    "- Message #" +                    (i + 1) +                    ";" +                    "\n";            crazyLogger.getLogger().append(string);        }        System.out.println(crazyLogger.getLogger());        System.out.println(crazyLogger.getDate("Message #2",crazyLogger.getLogger()));        System.out.println(crazyLogger.getMsg("05-07-1979 : 08-20",crazyLogger.getLogger()));    }}