package hometask4.task3;import java.io.*;public class Service {    static String readText(String fileName) {        StringBuilder stringBuilder = new StringBuilder();        int n;        try {            FileReader fileReader = new FileReader(fileName);            while ((n = fileReader.read()) != -1) {                stringBuilder.append((char) n);            }            fileReader.close();        } catch (IOException e) {            e.printStackTrace();        }        return stringBuilder.toString();    }    static void writeUTF8(String text, String fileName) {        try {            Writer writer = new BufferedWriter(new OutputStreamWriter(                    new FileOutputStream(fileName), "UTF-8"));            writer.write(text);            writer.close();        } catch (IOException e) {            e.printStackTrace();        }    }    static void writeUTF16(String fileNameUTF8, String fileNameUTF16) {        String text = readText(fileNameUTF8);        try {            Writer writer = new BufferedWriter(new OutputStreamWriter(                    new FileOutputStream(fileNameUTF16), "UTF-16"));            writer.write(text);            writer.close();        } catch (IOException e) {            e.printStackTrace();        }    }}