package hometask4.task4;import java.io.IOException;import java.io.Serializable;import java.util.ArrayList;import java.util.List;public class Film implements Serializable {    private String Title;    private int year;    private ArrayList<Actor> actorList;    public Film(String title, int year, ArrayList<Actor> actorList) {        Title = title;        this.year = year;        this.actorList = actorList;    }    void addActor(Actor actor) {        actorList.add(actor);        System.out.println("* Actor was added");    }    void removeActor() throws IOException {        Service.printActors(actorList);        System.out.println("* Choose index of the actor you would like to remove");        actorList.remove(Service.enterIndex(actorList.size()) - 1);        System.out.println("* Actor was removed");    }    public String getTitle() {        return Title;    }    public int getYear() {        return year;    }    public ArrayList<Actor> getActorList() {        return actorList;    }}