package hometask4.task4;import java.io.*;import java.util.ArrayList;public class Main{    public static void main(String[] args) {        ArrayList<Actor> actorArrayList = new ArrayList<>();        actorArrayList.add(new Actor("Jack","Nicholson"));        actorArrayList.add(new Actor("Louise", "Fletcher"));        actorArrayList.add(new Actor("William","Redfield"));        actorArrayList.add(new Actor("Shelley","Duvall"));        actorArrayList.add(new Actor("Scatman","Lloyd"));        ArrayList<Actor> cast1 = new ArrayList<>();        cast1.add(actorArrayList.get(0));        cast1.add(actorArrayList.get(1));        cast1.add(actorArrayList.get(2));        ArrayList<Actor> cast2 = new ArrayList<>();        cast2.add(actorArrayList.get(0));        cast2.add(actorArrayList.get(3));        cast2.add(actorArrayList.get(4));        ArrayList<Film> originalFilmArrayList = new ArrayList<>();        Film film1 = new Film("One Flew Over the Cuckooâ€™s Nest", 1975,cast1);        Film film2 = new Film("The Shining",1980,cast2);        originalFilmArrayList.add(film1);        originalFilmArrayList.add(film2);        Service.serialize(originalFilmArrayList);        ArrayList<Film> filmArrayList = Service.deserialize();        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));        try {            while (true) {                int i = 1;                System.out.format("%n* what would you like to do? Type command 1,2 or 3");                System.out.format("%n%d  Add an actor to a film", i++);                System.out.format("%n%d  Remove an actor from a film", i++);                System.out.format("%n%d  Exit%n%n", i);                int n = Service.enterInt(reader);                int k;                while (n < 1 || n > i) {                    System.out.format("%n* Type command 1,2,3 or 4...%n");                    n = Service.enterInt(reader);                }                switch (n) {                    case 1: {                        System.out.println("* Choose film");                        Service.printFilmTitles(filmArrayList);                        k = Service.enterIndex(filmArrayList.size());                        System.out.println("* Type Name");                        String name = reader.readLine();                        System.out.println("* Type Last name");                        String lastName = reader.readLine();                        filmArrayList.get(k - 1).addActor(new Actor(name,lastName));                        continue;                    }                    case 2: {                        System.out.println("* Choose film");                        Service.printFilmTitles(filmArrayList);                        k = Service.enterIndex(filmArrayList.size());                        filmArrayList.get(k - 1).removeActor();                        continue;                    }                    case 3: {                        System.out.println("* Goodbye!");                        reader.close();                        return;                    }                }            }        } catch (IOException e) {            System.out.println(e.getMessage());        }    }}