package hometask4.task4;import java.io.*;import java.util.ArrayList;public class Service {    static void serialize(ArrayList<Film> filmArrayList) {        try (ObjectOutputStream oos = new ObjectOutputStream(                new FileOutputStream("hometask4/src/main/resources/Serialize.txt"))) {            oos.writeObject(filmArrayList);            System.out.println("Запись произведена");        } catch (Exception ex) {            System.out.println(ex.getMessage());        }    }    static ArrayList<Film> deserialize() {        ArrayList<Film> filmArrayList = null;        try (ObjectInputStream ois = new ObjectInputStream(                new FileInputStream("hometask4/src/main/resources/Serialize.txt"))) {            filmArrayList = (ArrayList<Film>) ois.readObject();        } catch (Exception ex) {            System.out.println(ex.getMessage());        }        return filmArrayList;    }    static void printFilmTitles(ArrayList<Film> arrayList) {        int i = 1;        for (Film o : arrayList) {            System.out.println(i++ + " " + o.getTitle());        }    }    static void printActors(ArrayList<Actor> arrayList) {        int i = 1;        for (Actor o : arrayList) {            System.out.println(i++ + " " + o.getName() + " " + o.getLastName());        }    }    static int enterIndex(int size) throws IOException {        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));        int n = Service.enterInt(reader);        while (n < 1 || n > size) {            System.out.format("%n* Type index of the List you want to choose%n");            n = Service.enterInt(reader);        }        return n;    }    static int enterInt(BufferedReader reader) throws IOException {        String readLine = reader.readLine();        while (!isInteger(readLine)) {            System.out.println("Enter a valid number!");            readLine = reader.readLine();        }        return Integer.parseInt(readLine);    }    private static boolean isInteger(String s) {        try {            Integer.parseInt(s);        } catch (NumberFormatException | NullPointerException e) {            return false;        }        return true;    }}