package hometask7.task1;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;public class ConnectionDB {    public void getDriver() {        try {            try {                Class.forName("com.mysql.jdbc.Driver");            } catch (ClassNotFoundException e) {                System.out.println("Where is your MySQL JDBC Driver?");                e.printStackTrace();                return;            }            Connection conn = DriverManager.getConnection("jdbc:mysql://hostname:port/dbname", "username", "password");            conn.close();        } catch (SQLException e) {            e.printStackTrace();        }    }    private static Connection getDBConnection() {            Connection dbConnection = null;            try {                Class.forName(DB_DRIVER);            } catch (ClassNotFoundException e) {                System.out.println(e.getMessage());            }            try {                dbConnection = DriverManager.getConnection(DB_CONNECTION, DB_USER,DB_PASSWORD);                return dbConnection;            } catch (SQLException e) {                System.out.println(e.getMessage());            }            return dbConnection;        }}